package forlooping;

public class Alphabetabcde {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i=0;i<5;i++)
		{
			char alphabet='a';
			for(int j=0;j<=i;j++)
			{
			//	System.out.print((char)(alphabet+j)+" ");
				System.out.print(alphabet);
				alphabet++;
			}
			
			System.out.println(" ");
		}
		
		

	}

}
