package forlooping;

public class RevresAplhabet {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=0;i<=5;i++)
		{
			char alphabet='a';
			for(int j=5;j<=i;j--)
			{
				System.out.print((alphabet  +j)+" ");
				
			}
			
			System.out.println(" ");
		}
		
			}

	}


